mapper.update:
UPDATE custom_fields
custom_field_id = #{customFieldId}, create_user = #{createUser}, type = #{type}, create_time = #{createTime}, update_time = #{updateTime}, field_name_cn = #{fieldNameCn}, field_name_cn = #{fieldNameEn}, project_id = #{projectId}, modify_user = #{modifyUser}, length = #{length}, possible_value = #{possibleValue}, field_type = #{fieldType}, field_type_cn = #{fieldTypeCn},
WHERE custom_field_id = #{customFieldId}

mapper.select:
select

from custom_fields

    project_id = #{projectId} or type='sField'

        and `type` = #{type}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and field_name_cn = #{fieldNameCn}



        and field_name_en = #{fieldNameEn}



        and modify_user = #{modifyUser}


        and length = #{length}


        and possible_value = #{possibleValue}


        and field_type = #{fieldType}


        and `create_user` = #{createUser}



order by create_time desc

mapper.update:
update custom_fields
set possible_value = #{customFieldsDto.possibleValue},
    update_time    = #{customFieldsDto.updateTime},
    modify_user    = #{customFieldsDto.modifyUserId}
where custom_field_id = #{customFieldsDto.customFieldId}

mapper.select:
SELECT possible_value, custom_field_id FROM custom_fields WHERE (type = "sField" AND field_name_en = #{fieldName}) OR (project_id = #{projectId} AND field_name_cn = #{fieldName})
UNION
SELECT cf2.possible_value, cf2.custom_field_id FROM custom_fields cf1 JOIN custom_fields cf2 ON cf1.custom_field_id = cf2.linked_custom_field_id WHERE cf1.type = "sField" AND cf1.field_name_en = #{fieldName} AND cf2.type = "sCustom" AND cf2.project_id = #{projectId}
