mapper.select:
select
id, project_id, create_user_id, title, issue_status,
priority, env, browser, case_category, found_after_release, 
issue_version, introduced_version, is_legacy, fix_version, severity, test_device, 
COALESCE(runcase_id, '') as runcase_id, create_time, update_time, description, duration
from oneclick.issue


        and id = #{id}


        and project_id = #{projectId}


        and create_user_id = #{createUserId}


        and title = #{title}


        and status = #{status}


        and priority = #{priority}


        and env = #{env}


        and browser = #{browser}


        and platform = #{platform}


        and version = #{version}


        and case_category = #{caseCategory}


        and release_phase = #{releasePhase}


        and found_after_release = #{foundAfterRelease}


        and released_version = #{releasedVersion}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and description = #{description}


        ${filter}

SELECT 
    COUNT(*) as totalDefectsIntroduced,
    COUNT(CASE WHEN issue_version = #{analysisVersion} THEN 1 END) as currentVersionFound,
    COUNT(CASE WHEN issue_version != #{analysisVersion} THEN 1 END) as escapedDefects,
    ROUND(
        CASE 
            WHEN COUNT(*) = 0 THEN 0 
            ELSE COUNT(CASE WHEN issue_version != #{analysisVersion} THEN 1 END) * 100.0 / COUNT(*) 
        END, 2
    ) as escapeRate
FROM issue 
WHERE project_id = #{projectId} 
  AND introduced_version = #{analysisVersion}

      AND create_time >= #{startDate}


      AND create_time <= #{endDate}

SELECT id, title, description, severity, status, priority, introduced_version, issue_version, create_time, assignee, reporter, type FROM issue WHERE project_id = #{projectId} AND introduced_version = #{analysisVersion}
AND create_time >= #{startDate}
AND create_time <= #{endDate}
ORDER BY CASE WHEN issue_version != introduced_version THEN 0 ELSE 1 END, -- 逃逸缺陷优先显示 FIELD(severity, 'CRITICAL', 'HIGH', 'MEDIUM', 'LOW'), -- 按严重程度排序 create_time DESC -- 按创建时间倒序
