mapper.select:
select
id, project_id, title, priority, feature, description, execute_time, create_user_id, create_time, update_time,
browser, platform, version, case_category, test_type, external_link_id, env, test_condition, last_run_status,
`module`,test_device,test_data,test_method,test_status
from oneclick.test_case


        and id = #{id}


        and project_id = #{projectId}


        and title = #{title}


        and priority = #{priority}


        and feature = #{feature}


        and description = #{description}


        and execute_time = #{executeTime}


        and create_user_id = #{createUserId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and browser = #{browser}


        and platform = #{platform}


        and version = #{version}


        and case_category = #{caseCategory}


        and test_type = #{testType}


        and external_link_id = #{externalLinkId}


        and env = #{env}


        and test_condition = #{testCondition}


        and last_run_status = #{lastRunStatus}


        and test_status = #{testStatus}



        ${filter}

select id, title from test_case
project_id = #{projectId} and user_id = #{masterId}
and title like concat('%',#{title},'%')

SELECT COUNT(*)
FROM test_case
WHERE project_id = #{projectId}
  AND version = #{majorVersion}

SELECT COUNT(DISTINCT tc.id)
FROM test_case tc
JOIN test_cycle_join_test_case tcjtc ON tc.id = tcjtc.test_case_id
JOIN test_cycle tcycle ON tcjtc.test_cycle_id = tcycle.id
WHERE tc.project_id = #{projectId}
AND tc.version = #{majorVersion}
AND tcycle.version IN

    #{version}


AND tcjtc.test_cycle_id IN

    #{cycleId}


AND tcjtc.run_status IN (1, 2, 3, 4, 4.1, 4.2, 4.3, 4.4, 4.5)

SELECT tc.id as testCaseId, tc.title as testCaseTitle, tc.version as version, tcycle.id as testCycleId, tcycle.title as testCycleTitle, tcycle.env as testCycleEnv, tcycle.version as testCycleVersion, tcjtc.update_time as executionTime, tcjtc.run_status as executionStatus, tcjtc.run_count as runCount, tcjtc.id as runCaseId FROM test_case tc LEFT JOIN test_cycle_join_test_case tcjtc ON tc.id = tcjtc.test_case_id INNER JOIN test_cycle tcycle ON tcjtc.test_cycle_id = tcycle.id WHERE tc.project_id = #{projectId} AND tc.version = #{majorVersion} AND tcycle.version IN
#{version}
AND tcjtc.test_cycle_id IN
#{testCycleId}
ORDER BY tc.id, tcjtc.update_time DESC
