mapper.select:
select
id, project_id, title, priority, feature, description, execute_time, create_user_id, create_time, update_time,
browser, platform, version, case_category, test_type, external_link_id, env, test_condition, last_run_status,
`module`,test_device,test_data,test_method,test_status
from oneclick.test_case


        and id = #{id}


        and project_id = #{projectId}


        and title = #{title}


        and priority = #{priority}


        and feature = #{feature}


        and description = #{description}


        and execute_time = #{executeTime}


        and create_user_id = #{createUserId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and browser = #{browser}


        and platform = #{platform}


        and version = #{version}


        and case_category = #{caseCategory}


        and test_type = #{testType}


        and external_link_id = #{externalLinkId}


        and env = #{env}


        and test_condition = #{testCondition}


        and last_run_status = #{lastRunStatus}


        and test_status = #{testStatus}



        ${filter}

select id, title from test_case
project_id = #{projectId} and user_id = #{masterId}
and title like concat('%',#{title},'%')

SELECT COUNT(*)
FROM test_case
WHERE project_id = #{projectId}
AND version IN

    #{version}

SELECT COUNT(DISTINCT tc.id)
FROM test_case tc
JOIN test_cycle_join_test_case tcjtc ON tc.id = tcjtc.test_case_id
WHERE tc.project_id = #{projectId}
AND tc.version IN

    #{version}


AND tcjtc.test_cycle_id IN

    #{cycleId}


AND tcjtc.run_status IN (1, 2, 3, 4, 4.1, 4.2, 4.3, 4.4, 4.5)

SELECT tc.id as testCaseId, tc.title as testCaseTitle, tc.version as version, CASE WHEN tcjtc.test_case_id IS NOT NULL THEN true ELSE false END as executed, COUNT(tcjtc.id) as executionCount, MAX(tcjtc.update_time) as lastExecutionTime, tcycle.id as testCycleId, tcycle.title as testCycleTitle, tcycle.env as testCycleEnv, tcjtc.update_time as executionTime, tcjtc.run_status as executionStatus FROM test_case tc LEFT JOIN test_cycle_join_test_case tcjtc ON tc.id = tcjtc.test_case_id
AND tcjtc.test_cycle_id IN
#{cycleId}
LEFT JOIN test_cycle tcycle ON tcjtc.test_cycle_id = tcycle.id WHERE tc.project_id = #{projectId} AND tc.version IN
#{version}
GROUP BY tc.id, tc.title, tc.version, tcycle.id, tcycle.title, tcycle.env, tcjtc.update_time, tcjtc.run_status ORDER BY tc.id, tcjtc.update_time DESC
