mapper.select:
select id,
title
from test_cycle

    project_id = #{projectId}
    and user_id = #{masterId}

        and title like concat('%',#{title},'%')

select
id, create_user_id, project_id, title, test_cycle_status, run_status, last_run_date, last_modify,description,
test_version,
create_time, update_time, assign_to,notifiy_list
from oneclick.test_cycle


        and id = #{id}


        and create_user_id = #{userId}


        and project_id = #{projectId}


        and title = #{title}


        and test_cycle_status = #{status}


        and run_status = #{runStatus}


        and last_run_date = #{lastRunDate}


        and last_modify = #{lastModify}



        and test_version = #{version}


        and create_time = #{createTime}


        and update_time = #{updateTime}

SELECT version, title, id
FROM test_cycle
WHERE project_id = #{projectId}
  AND env = #{env}
  AND version = #{version}

SELECT *
FROM test_case ca
         JOIN test_cycle_join_test_case cy ON ca.id = cy.test_case_id
         JOIN test_cycle tc on cy.test_cycle_id = tc.id
WHERE ca.project_id = #{param1}
  AND ca.version = #{param2}
  and ca.env = #{param3}
  and tc.title in (#{param4})

SELECT `title`
FROM test_cycle
WHERE project_id = #{param1}
  AND env = #{param2}
  and current_release = #{param3}

SELECT id
FROM test_cycle
WHERE title = #{title}
  AND project_id = #{projectId}

SELECT tc.id AS test_cycle_id, tc.title AS test_cycle_title, tc.instance_count as test_cycle_instance, tc.allure_report_url, tca.id AS test_case_id, tca.title AS test_case_title, tca.run_status, tca.module AS test_case_module, tca.platform AS test_case_platform, tcjtc.execute_status FROM test_cycle tc LEFT JOIN test_cycle_join_test_case tcjtc ON tc.id = tcjtc.test_cycle_id LEFT JOIN test_case tca ON tcjtc.test_case_id = tca.id
tc.project_id= #{project_id}
AND tc.env= #{env}
AND tc.version= #{version}
AND tc.current_release= #{current_release}
WHERE tc.id IN (#{ids})
