mapper.insert:
INSERT INTO version_mapping (
    project_id, release_id, release_version, env, env_version, remark,
    create_time, update_time, create_user_id, update_user_id
) VALUES (
    #{projectId}, #{releaseId}, #{releaseVersion}, #{env}, #{envVersion}, #{remark},
    #{createTime}, #{updateTime}, #{createUserId}, #{updateUserId}
)

mapper.select:
SELECT 
FROM version_mapping


        AND project_id = #{projectId}


        AND release_id = #{releaseId}


        AND env = #{env}


        AND release_version LIKE CONCAT('%', #{releaseVersion}, '%')


        AND env_version LIKE CONCAT('%', #{envVersion}, '%')


ORDER BY create_time DESC
